export default function RootLayout({ children }) {
  const targetPath = usePathname();

  const headerAndFooter = ![
    "/dashboard",
    "/dashboard/resumes",
    "/dashboard/cover-letters",
    "/dashboard/jobs",
    "/dashboard/applications",
    "/dashboard/resumes/resume-edit",
  ].includes(targetPath);

  return (
    <html lang="en">
      <body className={inter.className}>
        {headerAndFooter ? (
          <MaxWidthWrapper>
            <Header />
            {children}
            <Footer />
          </MaxWidthWrapper>
        ) : (
          children
        )}
      </body>
    </html>
  );
}

